mapper.select:
select * from tw_lever_order where 1=1

    and uid = #{obj.uid}


    and (user_code like concat('%',#{obj.userCode},'%') or username like concat('%',#{obj.userCode},'%'))


    and company_id = #{obj.companyId}


    and path like concat('%',#{obj.employeeId},'%')

order by buytime desc
