mapper.select:
select * from (select id,uid,username,addtime,state from tw_online  where 1=1  and company_id  = #{obj.companyId}

    and department = #{obj.departmentId}


    and path like concat('%',#{obj.employeeId},'%')

group by uid,state order by state ASC,addtime  ) t group by uid order by state ASC,addtime desc
