mapper.select:
select * from tw_hyorder where 1=1  and company_id = #{obj.companyId}

    and username like concat('%',#{obj.username},'%')


    and (user_code like concat('%',#{obj.userCode},'%') or username like concat('%',#{obj.userCode},'%'))


    and hyzd = #{obj.hyzd}


    and status = #{obj.status}


    and order_no = #{obj.orderNo}


    and order_type = #{obj.orderType}



    and plantime = #{obj.plantimeStart}


    and time = #{obj.time}


    and department = #{obj.departmentId}


    and path like concat('%',#{obj.employeeId},'%')

order by id desc
