mapper.select:
select *, tu.username as followUsername from tw_mcd_hyorder tmh left join tw_user tu on tmh.follow_uid = tu.id where 1=1 and tmh.company_id = #{obj.companyId}

    and tmh.username like concat('%',#{obj.username},'%')


    and (tmh.user_code like concat('%',#{obj.userCode},'%') or tmh.username like concat('%',#{obj.userCode},'%'))


    and tu.username like concat('%',#{obj.followUsercode},'%')


    and (tu.user_code like concat('%',#{obj.followUsercode},'%') or tu.username like concat('%',#{obj.followUsercode},'%'))


    and hyzd = #{obj.hyzd}


    and tmh.status = #{obj.status}


    and order_no = #{obj.orderNo}



    and plantime BETWEEN #{obj.plantimeStart} AND #{obj.plantimeEnd}



    and department = #{obj.departmentId}


    and path like concat('%',#{obj.employeeId},'%')

order by tmh.id desc
